import { useMemo } from "react";
import jsPDF from "jspdf";

// ====================================================================
// --- INTERFACES & TIPOS CORE ---
// ====================================================================

export interface InvoiceItemData {
  "NOMBRE PRODUCTO": string;
  invoiceQuantity: number;
  invoicePrice: number;
}
export interface ClientData {
  name: string;
  company: string;
  email: string;
}
export interface InvoicePreviewData {
  blobUrl: string;
  quoteNumber: string;
  fileName: string;
}

type JsPdfWithMinimalConfig = jsPDF;

// ====================================================================
// --- CONSTANTES DE FORMATO (Espaciado Ajustado) ---
// ====================================================================

const COMPANY_INFO_RECEIPT = {
  name: "Cosmetics JG",
  address: "Calle 15, # 17 70 Local 201",
  phone: "3229365481",
  schedule: "Lunes a Sábado: 9:30 AM - 7:30 PM", // He cambiado 9:00 AM - 7:00 PM a 9:30 AM - 7:30 PM como en la imagen de prueba
};

// Reemplaza esta línea con tu imagen base64 real (pequeña, B/N, cuadrada)
const LOGO_BASE64 =
  "data:image/png;base64,"; // [IMAGEN BASE64 AQUÍ]

const RECEIPT_WIDTH_MM = 58;
const MARGIN_MM = 0.5;
const LOGO_SIZE_MM = 15;

// Ajuste de Tamaños de Fuente
const FONT_SIZE_XSMALL = 5;
const FONT_SIZE_SMALL_TINY = 6;
const FONT_SIZE_ADDRESS = 8;
const FONT_SIZE_NORMAL = 8;
const FONT_SIZE_LARGE = 11;
const FONT_SIZE_XLARGE = 14;
const LINE_HEIGHT = 5.5; // Espaciado Generoso
const HALF_LINE = LINE_HEIGHT * 0.5;

const CURRENCY_FORMAT_OPTIONS: Intl.NumberFormatOptions = {
  style: "currency",
  currency: "COP",
  maximumFractionDigits: 0,
};

// Caracteres separadores
const SEPARATOR_DASH = "--------------------------------------";
const SEPARATOR_EQUAL = "======================================";
const SEPARATOR_DOT = "........................................";

// ====================================================================
// 1. FUNCIÓN PURA (CORE) DE GENERACIÓN DE RECIBO TÉRMICO
// ====================================================================

export async function generateThermalReceiptPdfCore(
  invoiceItems: InvoiceItemData[],
  client: ClientData,
  subtotal: number,
  iva: number,
  total: number,
  applyIVA: boolean
): Promise<InvoicePreviewData> {
  if (invoiceItems.length === 0) {
    return Promise.reject(new Error("No hay productos para facturar."));
  }

  // --- PREPARACIÓN DE DATOS ---
  const finalClientName = client.name.trim() || "Cliente Preferencial";
  const now = new Date();
  const dateShort = now.toLocaleDateString("es-CO");
  const timeShort = now.toLocaleTimeString("es-CO", {
    hour: "2-digit",
    minute: "2-digit",
  });
  const quoteNumber = `FAC-${now.getFullYear()}-${String(now.getTime()).slice(
    -6
  )}`;
  const safeClientName = (client.name || "Cliente").replace(/[^a-z0-9]/gi, "_");
  const fileName = `Recibo_${quoteNumber}_${safeClientName}.pdf`;

  // --- INICIALIZACIÓN DEL DOCUMENTO ---
  const doc = new jsPDF({
    unit: "mm",
    format: [RECEIPT_WIDTH_MM, 300],
    orientation: "portrait",
  }) as JsPdfWithMinimalConfig;

  let currentY = MARGIN_MM + 4; // Buen margen inicial (4mm)

  // Funciones de utilidad para dibujar texto
  const centerText = (
    text: string,
    size: number,
    style: "normal" | "bold" = "normal"
  ) => {
    doc.setFontSize(size);
    doc.setFont("helvetica", style);
    doc.text(text, RECEIPT_WIDTH_MM / 2, currentY, { align: "center" });
    currentY += LINE_HEIGHT;
  };

  const printLeft = (
    text: string,
    size: number,
    style: "normal" | "bold" = "normal",
    x: number = MARGIN_MM
  ) => {
    doc.setFontSize(size);
    doc.setFont("helvetica", style);
    doc.text(text, x, currentY, { align: "left" });
  };
  const printRight = (
    text: string,
    size: number,
    style: "normal" | "bold" = "normal"
  ) => {
    doc.setFontSize(size);
    doc.setFont("helvetica", style);
    doc.text(text, RECEIPT_WIDTH_MM - MARGIN_MM, currentY, { align: "right" });
  };

  const separatorLine = (char: string = SEPARATOR_DASH) => {
    doc.setFontSize(FONT_SIZE_XSMALL);
    doc.setFont("helvetica", "normal");
    centerText(char, FONT_SIZE_XSMALL);
    currentY -= LINE_HEIGHT;
    currentY += HALF_LINE;
  };

  // --- 1. ENCABEZADO DE LA EMPRESA (Compactado) ---

  separatorLine(SEPARATOR_EQUAL);
  currentY += HALF_LINE * 0.5; // 🚨 Menos espacio después de la línea superior

  // Colocar Imagen/Logo
  if (LOGO_BASE64) {
    const logoX = RECEIPT_WIDTH_MM / 2 - LOGO_SIZE_MM / 2;
    doc.addImage(
      LOGO_BASE64,
      "PNG",
      logoX,
      currentY,
      LOGO_SIZE_MM,
      LOGO_SIZE_MM,
      undefined,
      "FAST"
    );
    currentY += LOGO_SIZE_MM + HALF_LINE * 2; // 🚨 Menos espacio después del logo
  }

  centerText(COMPANY_INFO_RECEIPT.name, FONT_SIZE_XLARGE, "bold");

  currentY -= HALF_LINE * 0.5;
  centerText(
    "RECIBO DE CAJA / FACTURA SIMPLIFICADA",
    FONT_SIZE_SMALL_TINY,
    "bold"
  );

  // Dirección destacada
  currentY -= HALF_LINE * 0.5;
  centerText(COMPANY_INFO_RECEIPT.address, FONT_SIZE_ADDRESS, "bold");

  // Datos secundarios compactos
  currentY -= HALF_LINE * 0.5;
  centerText(`Tel: ${COMPANY_INFO_RECEIPT.phone}`, FONT_SIZE_SMALL_TINY);

  currentY -= HALF_LINE * 0.5;
  centerText(`Horario: ${COMPANY_INFO_RECEIPT.schedule}`, FONT_SIZE_SMALL_TINY);

  currentY -= HALF_LINE * 0.5;
  centerText(`${dateShort} ${timeShort}`, FONT_SIZE_SMALL_TINY);

  currentY += HALF_LINE * 0.5; // 🚨 Menos espacio antes de la línea inferior
  separatorLine(SEPARATOR_EQUAL);

  // --- 2. DETALLES DE LA TRANSACCIÓN ---
  currentY += HALF_LINE;

  doc.setFont("helvetica", "bold");
  printLeft(`Factura N°: ${quoteNumber}`, FONT_SIZE_NORMAL);
  currentY += HALF_LINE;

  printLeft(`Cliente: ${finalClientName}`, FONT_SIZE_NORMAL);
  currentY += LINE_HEIGHT;

  // --- 3. TABLA DE PRODUCTOS ---

  const PADDING_QTY = MARGIN_MM + 1;
  const PADDING_DESC_START = MARGIN_MM + 8;
  const PADDING_PRICE_END = RECEIPT_WIDTH_MM - MARGIN_MM;

  doc.setFontSize(FONT_SIZE_NORMAL);
  doc.setFont("helvetica", "bold");
  doc.text("CANT", PADDING_QTY, currentY);
  doc.text("DESCRIPCION", PADDING_DESC_START, currentY);
  doc.text("TOTAL", PADDING_PRICE_END, currentY, { align: "right" });
  currentY += LINE_HEIGHT;

  currentY -= HALF_LINE;
  separatorLine(SEPARATOR_DASH);
  currentY += HALF_LINE;

  for (const item of invoiceItems) {
    const totalItem = item.invoicePrice * item.invoiceQuantity;
    const itemPriceFormatted = item.invoicePrice.toLocaleString(
      "es-CO",
      CURRENCY_FORMAT_OPTIONS
    );
    const totalItemFormatted = totalItem.toLocaleString(
      "es-CO",
      CURRENCY_FORMAT_OPTIONS
    );

    // Línea 1: Cantidad + Producto
    doc.setFontSize(FONT_SIZE_NORMAL);

    doc.text(item.invoiceQuantity.toString(), PADDING_QTY, currentY);
    doc.text(item["NOMBRE PRODUCTO"], PADDING_DESC_START, currentY);
    currentY += LINE_HEIGHT * 0.8;

    // Línea 2: Detalle de Precio Unitario y TOTAL
    doc.setFontSize(FONT_SIZE_XSMALL);
    doc.setFont("helvetica", "normal");

    const qtyPriceText = `@ ${itemPriceFormatted} Und.`;
    doc.text(qtyPriceText, PADDING_DESC_START, currentY);

    // Simulación de puntos
    const descWidth = doc.getTextWidth(qtyPriceText);
    let dotStart = PADDING_DESC_START + descWidth + 0.5;

    doc.setFontSize(FONT_SIZE_XSMALL);
    doc.setFont("helvetica", "normal");

    const totalWidth = doc.getTextWidth(totalItemFormatted);
    let dotEnd = PADDING_PRICE_END - totalWidth - 0.5;

    if (dotEnd > dotStart) {
      let dots = SEPARATOR_DOT.substring(
        0,
        Math.floor((dotEnd - dotStart) / doc.getTextWidth("."))
      );
      if (dots.length > 0) {
        doc.text(dots, dotStart, currentY);
      }
    }

    // Imprimir Total del Artículo (Negrita)
    doc.setFontSize(FONT_SIZE_NORMAL);
    doc.setFont("helvetica", "bold");
    doc.text(totalItemFormatted, PADDING_PRICE_END, currentY, {
      align: "right",
    });

    currentY += LINE_HEIGHT * 1.5;
  }

  // --- 4. SECCIÓN DE TOTALES ---

  currentY -= HALF_LINE;
  separatorLine(SEPARATOR_DASH);
  currentY += HALF_LINE;

  // Subtotal
  doc.setFontSize(FONT_SIZE_NORMAL);
  printLeft("Subtotal:", FONT_SIZE_NORMAL, "normal", MARGIN_MM + 8);
  doc.setFont("helvetica", "bold");
  printRight(
    subtotal.toLocaleString("es-CO", CURRENCY_FORMAT_OPTIONS),
    FONT_SIZE_NORMAL
  );
  currentY -= LINE_HEIGHT;

  if (applyIVA) {
    printLeft("IVA (19%):", FONT_SIZE_NORMAL, "normal", MARGIN_MM + 8);
    doc.setFont("helvetica", "bold");
    printRight(
      iva.toLocaleString("es-CO", CURRENCY_FORMAT_OPTIONS),
      FONT_SIZE_NORMAL
    );
    currentY -= LINE_HEIGHT;
  }

  currentY += LINE_HEIGHT * 0.5;
  separatorLine(SEPARATOR_EQUAL);
  currentY += LINE_HEIGHT;

  // TOTAL FINAL (Máximo impacto)
  doc.setFontSize(FONT_SIZE_LARGE + 2);
  centerText("TOTAL A PAGAR:", FONT_SIZE_LARGE + 2, "bold");

  doc.setFontSize(FONT_SIZE_LARGE + 5);
  centerText(
    total.toLocaleString("es-CO", CURRENCY_FORMAT_OPTIONS),
    FONT_SIZE_LARGE + 5,
    "bold"
  );
  currentY += LINE_HEIGHT * 2;

  // --- 5. PIE DE PÁGINA Y AGRADECIMIENTO (Persuasivo) ---

  centerText("TRANSACCIÓN AL CONTADO", FONT_SIZE_NORMAL, "bold");

  currentY -= HALF_LINE;
  separatorLine(SEPARATOR_DASH);
  currentY += HALF_LINE;

  // Mensaje Persuasivo
  centerText(
    `¡Gracias por tu compra, ${finalClientName.split(" ")[0]}!`,
    FONT_SIZE_NORMAL,
    "bold"
  );
  centerText(
    "Vuelve pronto, siempre es un placer atenderte.",
    FONT_SIZE_NORMAL
  );
  currentY += LINE_HEIGHT;

  // 6. **Generar Blob y devolver datos**
  const pdfBlob = doc.output("blob");
  const pdfUrl = URL.createObjectURL(pdfBlob);

  return Promise.resolve({
    blobUrl: pdfUrl,
    quoteNumber: quoteNumber,
    fileName: fileName,
  } as InvoicePreviewData);
}

// ====================================================================
// 2. HOOK DE REACT (useInvoicePdfReciboCaja)
// (Mantenido sin cambios)
// ====================================================================

export function useInvoicePdfReciboCaja(
  invoiceItems: InvoiceItemData[],
  client: ClientData,
  subtotal: number,
  iva: number,
  total: number,
  applyIVA: boolean
): { generatePdf: () => Promise<InvoicePreviewData> } {
  const generatePdf = useMemo(
    () => () => {
      return generateThermalReceiptPdfCore(
        invoiceItems,
        client,
        subtotal,
        iva,
        total,
        applyIVA
      );
    },
    [invoiceItems, client, subtotal, iva, total, applyIVA]
  );

  return { generatePdf };
}
